import time

def counting_sort(arr):
    max_value = max(arr)
    count = [0] * (max_value + 1)

    for num in arr:
        count[num] += 1

    sorted_list = []
    for i in range(len(count)):
        for j in range(count[i]):
            sorted_list.append(i)

    return sorted_list

